#ifndef STUBS_H
#define STUBS_H

#include <string>

const std::string STUBS =         "\n        /*\n         * Stubs for built-in classes of Quack. We do not generate code\n         * for these.  These are used only for static semantics checking.\n         * Their real code is hand-written C code.\n         */\n\n         class Obj() {\n         	def STR() : String { }\n            def PRINT() { }\n            def EQUALS(other: Obj): Boolean {} // Default is object identity\n         }\n\n         class Nothing() {\n         	def STR(): String { } // <nothing>\n         }\n\n         class String() {\n         	def STR(): String { } // this\n            def EQUALS(other: Obj): Boolean { }   // ==\n            def LESSER(other: String): Boolean { }     // <\n            def GREATER(other: String): Boolean { }     // >\n            def ATLEAST(other: String): Boolean { }  // >=\n            def ATMOST(other: String): Boolean { }   // <=\n            def PLUS(other: String): String { }      // +\n         }\n\n         class Boolean() {\n         	def STR(): String { } // true or false\n         }\n\n         class Int() {\n         	def STR(): String { } // value\n            def EQUALS(other: Obj): Boolean { }   // ==\n			def LESSER(other: Int): Boolean { }       // <\n            def GREATER(other: Int): Boolean { }       // >\n            def ATLEAST(other: Int): Boolean { }    // >=\n            def ATMOST(other: Int): Boolean { }     // <=\n            def PLUS(other: Int): Int { }   // this + right\n            def MINUS(other: Int): Int { }  // this - right\n            def TIMES(other: Int): Int { }  // this * right\n            def DIVIDE(other: Int): Int { }    // this / right\n            def NEGATE(other: Int): Int { } // -this\n         }\n        ";

#endif //STUBS_H